const min = 0
const max = 1;

rnd = Math.floor(Math.random() * (max - min + 1)) + min;

if( rnd == 0)
    return [msg, null];

if( rnd == 1)
    return [null, msg];

if( msg.reset )
{
    context.set('msg_old', msg)
}

if( msg.complete )
{
    msg_old = context.get('msg_old')
    return msg_old
}

var  s = String(msg.payload);

msg.payload = {}
var result;

result = s.match(/(?:^|&)t=(\d\d\d\d)(\d\d)(\d\d)T(\d\d)(\d\d)\d?\d?(?:$|&)/);
if( result ){
    msg.payload.date = new Date( result[1], result[2]-1, result[3], result[4], result[5] );
} else
    return [null, msg];

result = s.match(/(?:^|&)s=([\d\.]*)(?:$|&)/);
if( result ){
    msg.payload.price = result[1]
} else 
    return [null, msg];

result = s.match(/(?:^|&)fn=(\d*)(?:$|&)/);
if( result ){
    msg.payload.fn = result[1]
} else 
    return [null, msg];

result = s.match(/(?:^|&)fp=(\d*)(?:$|&)/);
if( result ){
    msg.payload.fp = result[1]
} else 
    return [null, msg];

result = s.match(/(?:^|&)i=(\d*)(?:$|&)/);
if( result ){
    msg.payload.fd = result[1]
} else 
    return [null, msg];

result = s.match(/(?:^|&)n=(\d*)(?:$|&)/);
if( result ){
    msg.payload.n = result[1]
} else 
    return [null, msg];

return [msg, null]

